// PriceComparisonTable.js

import React from "react";
import { Card, Typography } from "@material-tailwind/react";

function PriceComparisonTable({ data }) {
  const renderComparison = (productA, productB) => {
    const priceA = parseInt(productA.price.replace("₹", "").replace(",", ""));
    const priceB = parseInt(productB.price.replace("₹", "").replace(",", ""));
    const ratingA = parseFloat(productA.rating);
    const ratingB = parseFloat(productB.rating);

    if (priceA > priceB && ratingA > ratingB) {
      return `${productA.company} has higher price and rating compared to ${productB.company}`;
    } else if (priceA < priceB && ratingA < ratingB) {
      return `${productA.company} has lower price and rating compared to ${productB.company}`;
    } else {
      return `${productA.company} has mixed comparison with ${productB.company}`;
    }
  };

  return (
    <Card className="mt-10 px-8 h-full w-full overflow-scroll">
      <table className="w-full min-w-max table-auto text-left">
        <thead>
          <tr>
            <th className="border-b border-blue-gray-100 bg-blue-gray-50 p-4">
              <Typography
                variant="small"
                color="blue-gray"
                className="font-normal leading-none opacity-70"
              >
                Product Comparison
              </Typography>
            </th>
          </tr>
        </thead>
        <tbody>
          {data.map((productA, indexA) => {
            const correspondingProduct = data.find(
              (productB, indexB) => indexB === indexA && productB.company === "Amazon"
            );
            if (correspondingProduct) {
              return (
                <tr key={`${productA.name}-${correspondingProduct.name}`}>
                  <td className="p-4">
                    {renderComparison(productA, correspondingProduct)}
                  </td>
                </tr>
              );
            }
            return null;
          })}
        </tbody>
      </table>
    </Card>
  );
}

export default PriceComparisonTable;
