import React from "react";
import { Card, Typography } from "@material-tailwind/react";

function PriceComparisonTable({ data }) {
  const amazonPriceComparison = (amazonPrice, flipkartPrice) => {
    if (amazonPrice > flipkartPrice) {
      return "Amazon has higher price";
    } else if (amazonPrice < flipkartPrice) {
      return "Amazon has lower price";
    } else {
      return "Prices are equal";
    }
  };

  const flipkartPriceComparison = (flipkartPrice, amazonPrice) => {
    if (flipkartPrice > amazonPrice) {
      return "Flipkart has higher price";
    } else if (flipkartPrice < amazonPrice) {
      return "Flipkart has lower price";
    } else {
      return "Prices are equal";
    }
  };

  const amazonRatingComparison = (amazonRating, flipkartRating) => {
    if (amazonRating > flipkartRating) {
      return "Amazon has higher rating";
    } else if (amazonRating < flipkartRating) {
      return "Amazon has lower rating";
    } else {
      return "Ratings are equal";
    }
  };

  const flipkartRatingComparison = (flipkartRating, amazonRating) => {
    if (flipkartRating > amazonRating) {
      return "Flipkart has higher rating";
    } else if (flipkartRating < amazonRating) {
      return "Flipkart has lower rating";
    } else {
      return "Ratings are equal";
    }
  };

  return (
    <Card className="mt-10 px-8 h-full w-full overflow-scroll">
      <table className="w-full min-w-max table-auto text-left">
        <thead>
          <tr>
            <th className="border-b border-blue-gray-100 bg-blue-gray-50 p-4">
              <Typography
                variant="small"
                color="blue-gray"
                className="font-normal leading-none opacity-70"
              >
                Product Name
              </Typography>
            </th>
            <th className="border-b border-blue-gray-100 bg-blue-gray-50 p-4">
              <Typography
                variant="small"
                color="blue-gray"
                className="font-normal leading-none opacity-70"
              >
                Company
              </Typography>
            </th>
            <th className="border-b border-blue-gray-100 bg-blue-gray-50 p-4">
              <Typography
                variant="small"
                color="blue-gray"
                className="font-normal leading-none opacity-70"
              >
                Price Comparison
              </Typography>
            </th>
            <th className="border-b border-blue-gray-100 bg-blue-gray-50 p-4">
              <Typography
                variant="small"
                color="blue-gray"
                className="font-normal leading-none opacity-70"
              >
                Rating Comparison
              </Typography>
            </th>
          </tr>
        </thead>
        <tbody>
          {data.map(({ name, company, price, rating }) => {
            const flipkartPrice = data.find(item => item.name === name && item.company === "Flipkart").price;
            const amazonPrice = data.find(item => item.name === name && item.company === "Amazon").price;
            const flipkartRating = data.find(item => item.name === name && item.company === "Flipkart").rating;
            const amazonRating = data.find(item => item.name === name && item.company === "Amazon").rating;

            return (
              <tr key={`${name}-${company}`}>
                <td className="p-4">{name}</td>
                <td className="p-4">{company}</td>
                <td className="p-4">
                  {company === "Amazon"
                    ? amazonPriceComparison(price, flipkartPrice)
                    : flipkartPriceComparison(price, amazonPrice)}
                </td>
                <td className="p-4">
                  {company === "Amazon"
                    ? amazonRatingComparison(rating, flipkartRating)
                    : flipkartRatingComparison(rating, amazonRating)}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </Card>
  );
}

export default PriceComparisonTable;
