import React, { useState, useEffect } from 'react';
import { NavbarDark } from './components/NavBar';
import { TableWithStripedColumns } from './components/DataTable';
import axios from 'axios';

function App() {
  const [scrapedData, setScrapedData] = useState([]);
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async (query) => {
    try {
      const response = await fetch('http://127.0.0.1:5000/api/data');
      const jsonData = await response.json();

      if (jsonData) {
        const combinedData = [...jsonData.flipkart_data, ...jsonData.amazon_data];

        const filteredResults = combinedData.filter((item) => {
          return (
            item.name.toLowerCase().includes(query.toLowerCase()) ||
            item.company.toLowerCase().includes(query.toLowerCase())
          );
        });

        setSearchResults(filteredResults);
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  useEffect(() => {
    async function fetchData() {
      try {
        const response = await axios.get("http://localhost:5000/api/data");
        if (response.data.flipkart_data && response.data.amazon_data) {
          setScrapedData([...response.data.flipkart_data, ...response.data.amazon_data]);
        }
      } catch (error) {
        console.error("Error:", error);
      }
    }
    fetchData();
  }, []);


  return (
    <div>
      <header>
        <NavbarDark onSearch={handleSearch} />
        <TableWithStripedColumns scrapedData={scrapedData} searchResults={searchResults} />
      </header>
    </div>
  );
}

export default App;
