import React from "react";
import { Card, Typography } from "@material-tailwind/react";

function PriceComparisonTable({ data }) {
  const renderComparison = (amazonProduct, flipkartProduct) => {
    const amazonPrice = parseInt(amazonProduct.price.replace(/\D/g, ""));
    const flipkartPrice = parseInt(flipkartProduct.price.replace(/\D/g, ""));
    const amazonRating = parseFloat(amazonProduct.rating);
    const flipkartRating = parseFloat(flipkartProduct.rating);

    if (amazonRating > flipkartRating && amazonPrice < flipkartPrice) {
      return `Amazon's product has higher rating and lower price compared to Flipkart's product.`;
    } else if (amazonRating < flipkartRating && amazonPrice > flipkartPrice) {
      return `Flipkart's product has higher rating and lower price compared to Amazon's product.`;
    } else {
      return `No clear comparison between Amazon's product and Flipkart's product.`;
    }
  };

  return (
    <Card className="mt-10 px-8 h-full w-full overflow-scroll">
      <table className="w-full min-w-max table-auto text-left">
        <thead>
          <tr>
            <th className="border-b border-blue-gray-100 bg-blue-gray-50 p-4">
              <Typography
                variant="small"
                color="blue-gray"
                className="font-normal leading-none opacity-70"
              >
                Product Comparison
              </Typography>
            </th>
          </tr>
        </thead>
        <tbody>
          {data.amazon_data.map((amazonProduct) => {
            return data.flipkart_data.map((flipkartProduct) => (
              <tr key={`${amazonProduct.name}-${flipkartProduct.name}`}>
                <td className="p-4">
                  {renderComparison(amazonProduct, flipkartProduct)}
                </td>
              </tr>
            ));
          })}
        </tbody>
      </table>
    </Card>
  );
}

export default PriceComparisonTable;
