import React,{ useState,useEffect } from 'react'

import SearchBar from './components/SearchBar';
import DataTable from './components/DataTable';
// import PriceChart from './components/PriceChart';

function App() {
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = (searchTerm) => {
    // Simulate fetching data from the server based on the search term
    // Replace this with your actual API call
    const fakeData = [
      { id: 1, name: 'Product 1', price: 100, rating: 4.5, configuration: 'Specs 1' },
      { id: 2, name: 'Product 2', price: 150, rating: 4.2, configuration: 'Specs 2' },
      // Add more product data here
    ];

    setSearchResults(fakeData); // Replace with actual data
  };





  const [data,setdata] = useState('');


  useEffect(() =>{
    async function fetchData(){
      const response = await fetch('http://127.0.0.1:5000/api/data');
      const jsonData = await response.json();
      setdata(jsonData.message);
    }
    fetchData();
  })
  return (
    <div>
      <header>
        <p>{data}</p>

        <SearchBar onSearch={handleSearch} />
      <DataTable products={searchResults} />
      {/* <PriceChart products={searchResults} /> */}

      </header>
    </div>
  )
}

export default App
